<template>  <div>    count: <span @click="decrement">点我➖➖</span>{{count.count}}<span @click="increment">点我➕➕</span>  </div></template><script type='text/ecmascript-6'>  import { mapActions, mapState } from 'vuex';  export default {    computed: {      ...mapState(['count'])  // 通过mapState()方法将state注入到组件中    },    methods: {      ...mapActions(['increment', 'decrement']),      conso () {        console.log(this.$store.state.count);      }    },    created () {      this.conso();    }  };</script><style scoped></style>