<template>  <div>    <p>counter: <span @click="minus">点我➖ </span>{{count}}<span @click="add">点我➕</span></p>    <p>{{products.length}}</p>    <p>{{checkoutStatus}}</p>    <p @click="_click">{{addToAdd}}</p>    <p @click="_click">组件自己的方法，点击我！！！</p>  </div></template><script type='text/ecmascript-6'>  import { mapActions, mapGetters } from 'vuex';  export default {    methods: {      ...mapActions(['add', 'minus']),      _click () {        alert('组件自己的方法');      }    },    computed: mapGetters({      count: 'counter',      addToAdd: 'addToAdd',      products: 'allProducts',      checkoutStatus: 'checkoutStatus'    })  };</script><style scoped></style>