<template>  <div>    <p>counter: <span @click="minus">点我➖ </span>{{count}}<span @click="add">点我➕</span></p>    <p>{{products.length}}</p>    <p>{{checkoutStatus}}</p>    <p @click="_click">{{addToAdd}}</p>    <p @click="_click">组件自己的方法，点击我！！！</p>    <p>组件自身的数据：{{updateData}}</p>  </div></template><script type='text/ecmascript-6'>  import { mapActions, mapGetters } from 'vuex';  export default {    data () {   // 定义组件自身的数据      return {        myData: 1      };    },    methods: {  // 将actions方法函数，混进组件当中      ...mapActions(['add', 'minus']),      _click () { // 定义组件自身的方法        alert('组件自己的方法');      }    },    computed: {      updateData () { // 计算组件自身的数据        return this.myData * 5;      },      ...mapGetters({ //  获取statea        count: 'counter',        addToAdd: 'addToAdd',        products: 'allProducts',        checkoutStatus: 'checkoutStatus'      })    }  };</script><style scoped></style>