<template>  <div>    <p>counter: <span @click="minus">点我➖ </span>{{count}}<span @click="add">点我➕</span></p>    <p>{{products.length}}</p>    <p>{{checkoutStatus}}</p>    <p @click="_click">{{addToAdd}}</p>    <p @click="_click">组件自己的方法，点击我！！！</p>    <p>组件自身的数据状态：{{updateData}}</p>  </div></template><script type='text/ecmascript-6'>  import { mapActions, mapGetters } from 'vuex';  export default {    data () {      return {        myData: 1      };    },    methods: {      ...mapActions(['add', 'minus']),      _click () {        alert('组件自己的方法');      }    },    computed: {      updateData () {        return this.myData * 5;      },      ...mapGetters({        count: 'counter',        addToAdd: 'addToAdd',        products: 'allProducts',        checkoutStatus: 'checkoutStatus'      })    }  };</script><style scoped></style>