<template>  <ul>    <li v-for="p in products">      {{p.title}} - {{p.price | currency}}      <br/>      <button        :disable="!p.inventory"        @click="addToCart(p)"      >add to cart</button>    </li>  </ul></template><script type='text/ecmascript-6'>  import { mapGetters, mapActions } from 'vuex';  export default {    methods: mapActions(['addToCart']),    computed: mapGetters({      products: 'allProducts'    }),    created () {      this.$store.dispatch('getAllProducts');    }  };</script><style scoped></style>