<template>  <div class="container">    <span class="title">{{item}}</span><input v-model="msg" :placeholder='val' class="value" />    <p><button @click="_emit">点击传递数据给父组件</button></p>  </div></template><script type='text/ecmascript-6'>  export default {    data () {      return {        msg: ''      };    },    methods: {      _emit () {        this.$emit('communication', this.msg);  // 定义需要向父组件传递的数据的名称communication，以及传递的参数msg,通过$emit()方法向上传递      }    },    props: ['item', 'val']    // 接收父组件传递的属性  };</script><style scoped>  .container{    overflow: hidden;  }  .title{    float: left;    width:20%;    padding: 8px 10px;  }  .value{    float: right;    width:60%;    padding: 8px 10px;  }  button{    padding:10px 10px;  }</style>